import React, { useState, useRef } from 'react';

import ContactView from './ContactView';

import AddUser from './AddUser';
import Button from 'antd/es/button';
import Drawer from 'antd/es/drawer';
import { useEffect } from 'react';
import ContactBody from './ContactBody';
import { Col } from 'antd';

const ContactList = (props) => {
  const [viewUser, setviewUser] = useState(0);
  const [addUser, setAddUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [currentUserMsg, setCurrentUserMsg] = useState([]);
  const [otherUser, setOtherUser] = useState(null);
  const mainRef = useRef(null);

  const [visible, setVisible] = useState(false);
  const [message, setMessage] = useState('');

  const mms = currentUserMsg?.messages;

  useEffect(() => {
    if (mainRef && mainRef.current) {
      mainRef.current.scrollIntoView();
    }
  }, [mms]);

  const addEditUser = (type, data, key) => {
    if (type === 'add') {
      props.setAddedList(data);
    }
  };

  const editUser = (e, contact) => {
    e.stopPropagation();
    setAddUser(contact);
  };

  const sendMessage = () => {
    if (messages === '') {
      return;
    }
    let newMessages = messages;
    let index = newMessages.findIndex(
      (val) =>
        (val.key1 === curentUser.key || val.key2 === curentUser.key) &&
        (val.key1 === otherUser.key || val.key2 === otherUser.key)
    );

    if (index >= 0) {
      let newMessageData = { ...newMessages[index] };
      let messageQueue = [...newMessageData.messages];
      messageQueue.push({
        key: curentUser.key,
        message: message,
        id: props.v4(),
      });
      newMessageData.messages = messageQueue;
      newMessages[index] = newMessageData;
      setCurrentUserMsg(newMessageData);
      setMessages(newMessages);
    } else {
      let newMessageData = {
        key1: curentUser.key,
        key2: otherUser.key,
        messages: [{ key: curentUser.key, message: message, id: props.v4() }],
      };
      newMessages.push(newMessageData);
      setCurrentUserMsg(newMessageData);
      setMessages(newMessages);
    }
    setMessage('');
  };

  const setMessageData = (otherUsr) => {
    let newMessages = messages.filter(
      (val) =>
        (val.key1 === curentUser.key || val.key2 === curentUser.key) &&
        (val.key1 === otherUsr.key || val.key2 === otherUsr.key)
    );

    if (newMessages.length) {
      setCurrentUserMsg(newMessages[0]);
    } else {
      setCurrentUserMsg([]);
    }
  };

  return (
    <>
      <div className="add__user">
        <AddUser
          randomNumber={props.randomNumber}
          colors={props.colors}
          addEditUser={addEditUser}
          addUser={addUser}
          editUser={editUser}
          setAddUser={setAddUser}
        />
      </div>
      <div className="container">
        <Col lg={16} md={16} sm={24} xs={24} style={{ padding: '16px' }}>
          <table>
            <thead>
              <tr>
                <th>
                  <h3>Full Name</h3>
                </th>
                <th>
                  <h3>Company Name</h3>
                </th>
                <th>
                  <h3>Phone</h3>
                </th>
                <th>
                  <h3>Edit</h3>
                </th>
                <th>
                  <h3>Message</h3>
                </th>
              </tr>
            </thead>
            <tbody>
              <ContactBody
                list={props.list}
                curentUser={curentUser}
                setviewUser={setviewUser}
                editUser={editUser}
                setOtherUser={setOtherUser}
                setVisible={setVisible}
                setMessageData={setMessageData}
              />
            </tbody>
          </table>
        </Col>

        <Col lg={8} mg={8} sm={12} xs={24}>
          <Drawer
            title={otherUser?.fullName}
            placement="right"
            closable={false}
            onClose={() => setVisible(false)}
            visible={visible}
            width={'90%'}
          >
            <div className="message__container">
              <div className="messages">
                {currentUserMsg?.messages?.map((msg, index) => {
                  let alignMsg =
                    msg.key === curentUser.key ? 'flex-end' : 'flex-start';
                  if (currentUserMsg?.messages.length === index + 1) {
                    return (
                      <div
                        key={msg.id}
                        className="message__bg"
                        style={{ alignSelf: alignMsg }}
                        ref={mainRef}
                      >
                        {msg.message}
                      </div>
                    );
                  }
                  return (
                    <div
                      key={msg.id}
                      className="message__bg"
                      style={{ alignSelf: alignMsg }}
                    >
                      {msg.message}
                    </div>
                  );
                })}
              </div>
              <div className="send__massage--cotainer">
                <form
                  className="message__input"
                  onSubmit={(e) => {
                    e.preventDefault();
                    sendMessage();
                  }}
                >
                  <input
                    type="text"
                    placeholder="Message"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    className="message"
                  />

                  <Button type="primary" onClick={sendMessage}>
                    Send
                  </Button>
                </form>
              </div>
            </div>
          </Drawer>

          <div className="contact__view">
            <ContactView
              contact={props.list[viewUser] || null}
              color={props.randomNumber()}
              colors={props.colors}
            />
          </div>
        </Col>
      </div>
    </>
  );
};

export default ContactList;
