import React, { useState } from 'react';
import { Layout, Menu } from 'antd';
import { v4 } from 'uuid';
import Select from 'antd/es/select';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  UserOutlined,
  VideoCameraOutlined,
  UploadOutlined,
} from '@ant-design/icons';
import ContactList from '../Components/ContactList';

const { Header, Sider, Content } = Layout;

const SiderDemo = () => {
  const [collapsed, setCollapsed] = useState(true);
  const { Option } = Select;

  const toggle = () => {
    console.log('asdfasd');
    setCollapsed(!collapsed);
  };
  const colors = [
    '#29bf12',
    '#e6c229',
    '#f17105',
    '#d11149',
    '#6610f2',
    '#1a8fe3',
  ];
  const randomNumber = () => {
    return Math.ceil(Math.random() * colors.length);
  };
  const setAddedList = () => {
    setList([...list, data]);
  };
  const [list, setList] = useState([
    {
      key: v4(),
      companyName: 'ABB Tech',
      email: 'st2278@gmail.com',
      fullName: 'John Doe',
      phone: 8940651334,
      avatarName: 'JD',
      color: colors[randomNumber()],
    },
    {
      key: v4(),
      companyName: 'XYZ Tech',
      email: 'sj@gmail.com',
      fullName: 'Stuart John',
      phone: 8940651334,
      avatarName: 'SJ',
      color: colors[randomNumber()],
    },
  ]);
  return (
    <Layout>
      {collapsed}
      <Sider trigger={null} collapsible collapsed={collapsed}>
        {collapsed ? (
          <MenuUnfoldOutlined
            style={{ fontSize: '20px', color: '#fff', padding: '10px' }}
            onClick={toggle}
          />
        ) : (
          <MenuFoldOutlined
            style={{ fontSize: '20px', color: '#fff', padding: '10px' }}
            onClick={toggle}
          />
        )}
        <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
          <Menu.Item key="1" icon={<UserOutlined />}>
            nav 1
          </Menu.Item>
          <Menu.Item key="2" icon={<VideoCameraOutlined />}>
            nav 2
          </Menu.Item>
          <Menu.Item key="3" icon={<UploadOutlined />}>
            nav 3
          </Menu.Item>
        </Menu>
      </Sider>
      <Layout className="site-layout">
        <Header className="site-layout-background" style={{ padding: 0 }}>
          <Select
            value={curentUser.key}
            className="select-after"
            onChange={(e) => {
              const user = list.find((val) => val.key === e);
              setCurrentUser(user);
            }}
          >
            {list.map((usVal) => (
              <Option key={usVal.key} value={usVal.key}>
                {usVal.fullName}
              </Option>
            ))}
          </Select>
        </Header>
        <Content
          className="site-layout-background"
          style={{
            margin: '24px 16px',
            padding: 24,
            minHeight: 280,
          }}
        >
          <ContactList
            list={list}
            colors={colors}
            randomNumber={randomNumber}
          />
        </Content>
      </Layout>
    </Layout>
  );
};

export default SiderDemo;
